// toolbar.js

import { useState } from 'react';
import { DraggableNode } from './draggableNode';
import { useStore } from './store';

export const PipelineToolbar = () => {
    const savePipeline = useStore((s) => s.savePipeline);
    const loadPipeline = useStore((s) => s.loadPipeline);
    const clearPipeline = useStore((s) => s.clearPipeline);
    const undo = useStore((s) => s.undo);
    const redo = useStore((s) => s.redo);

    const [searchTerm, setSearchTerm] = useState('');
    const [collapsedCategories, setCollapsedCategories] = useState({});

    const toggleCategory = (categoryName) => {
        setCollapsedCategories(prev => ({
            ...prev,
            [categoryName]: !prev[categoryName]
        }));
    };

    const nodeCategories = {
        Input: [
            { type: 'customInput', label: 'Input' },
            { type: 'number', label: 'Number' },
            { type: 'boolean', label: 'Boolean' }
        ],
        Processing: [
            { type: 'llm', label: 'LLM' },
            { type: 'text', label: 'Text' },
            { type: 'math', label: 'Math' },
            { type: 'api', label: 'API' },
            { type: 'delay', label: 'Delay' }
        ],
        Output: [
            { type: 'customOutput', label: 'Output' }
        ]
    };

    const filteredCategories = Object.entries(nodeCategories).reduce((acc, [categoryName, nodes]) => {
        const filteredNodes = nodes.filter(node =>
            node.label.toLowerCase().includes(searchTerm.toLowerCase())
        );
        if (filteredNodes.length > 0 || searchTerm === '') {
            acc[categoryName] = filteredNodes.length > 0 ? filteredNodes : nodes;
        }
        return acc;
    }, {});

    return (
        <div className="toolbar">
            <div className="node-palette">
                <div className="search-container">
                    <input
                        type="text"
                        placeholder="Search nodes..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="search-input"
                    />
                </div>
                {Object.entries(filteredCategories).map(([categoryName, nodes]) => (
                    <div key={categoryName} className="category">
                        <h4
                            onClick={() => toggleCategory(categoryName)}
                            className="category-header"
                        >
                            {categoryName}
                            <span className={`toggle-icon ${collapsedCategories[categoryName] ? 'collapsed' : 'expanded'}`}>
                                â–¼
                            </span>
                        </h4>
                        {!collapsedCategories[categoryName] && (
                            <div className="category-content">
                                {nodes.map(node => (
                                    <DraggableNode key={node.type} type={node.type} label={node.label} />
                                ))}
                            </div>
                        )}
                    </div>
                ))}
            </div>
            <div className="actions">
                <button onClick={undo} className="action-btn">Undo</button>
                <button onClick={redo} className="action-btn">Redo</button>
                <button onClick={savePipeline} className="action-btn">Save</button>
                <button onClick={loadPipeline} className="action-btn">Load</button>
                <button onClick={clearPipeline} className="action-btn clear">Clear</button>
            </div>
        </div>
    );
};
