import { PipelineToolbar } from './toolbar';
import { PipelineUI } from './ui';
import SubmitButton from './submit';
import { useStore } from './store';
import { useMemo } from 'react';
import './index.css';

function App() {
  const nodes = useStore((s) => s.nodes);
  const edges = useStore((s) => s.edges);

  const stats = useMemo(() => {
    const nodeCount = nodes.length;
    const edgeCount = edges.length;

    // Calculate complexity score based on nodes, edges, and node types
    const complexityScore = Math.round(
      nodeCount * 2 + // Base score per node
      edgeCount * 1.5 + // Connections add complexity
      nodes.filter(node => ['llm', 'api', 'math'].includes(node.type)).length * 3 // Complex nodes add more
    );

    // Estimate execution time based on node types
    const executionTime = nodes.reduce((total, node) => {
      switch (node.type) {
        case 'llm': return total + 2000; // 2 seconds for LLM calls
        case 'api': return total + 500; // 0.5 seconds for API calls
        case 'delay': return total + (node.data?.delay || 1000); // Custom delay
        case 'math': return total + 100; // 0.1 seconds for calculations
        default: return total + 50; // 0.05 seconds for simple operations
      }
    }, 0);

    return {
      nodeCount,
      edgeCount,
      complexityScore,
      executionTime: Math.round(executionTime / 100) / 10 // Convert to seconds with 1 decimal
    };
  }, [nodes, edges]);

  return (
    <div className="app-container">
      <header className="header">
        <h1>VectorShift Pipeline Builder</h1>
        <div className="stats">
          <span>Nodes: {stats.nodeCount}</span>
          <span>Edges: {stats.edgeCount}</span>
          <span>Complexity: {stats.complexityScore}</span>
          <span>Est. Time: {stats.executionTime}s</span>
        </div>
        <PipelineToolbar />
      </header>
      <div className="pipeline-ui">
        <PipelineUI />
        <SubmitButton />
      </div>
    </div>
  );
}

export default App;
