// ui.js
// Displays the drag-and-drop UI
// --------------------------------------------------

import { useState, useRef, useCallback } from 'react';
import ReactFlow, { Controls, Background, MiniMap } from 'reactflow';
import { useStore } from './store';
import { shallow } from 'zustand/shallow';
import LLMNode from './nodes/llmNode';
import { InputNode } from './nodes/inputNode';
import { OutputNode } from './nodes/outputNode';
import { TextNode } from './nodes/textNode';
import { NumberNode } from './nodes/numberNode';
import { BooleanNode } from './nodes/booleanNode';
import { MathNode } from './nodes/mathNode';
import { ApiNode } from './nodes/apiNode';
import { DelayNode } from './nodes/delayNode';



import 'reactflow/dist/style.css';

const gridSize = 20;
const proOptions = { hideAttribution: true };
const nodeTypes = {
  customInput: InputNode,
  llm: LLMNode,
  customOutput: OutputNode,
  text: TextNode,
  number: NumberNode,
  boolean: BooleanNode,
  math: MathNode,
  api: ApiNode,
  delay: DelayNode,
};

const selector = (state) => ({
  nodes: state.nodes,
  edges: state.edges,
  getNodeID: state.getNodeID,
  addNode: state.addNode,
  onNodesChange: state.onNodesChange,
  onEdgesChange: state.onEdgesChange,
  onConnect: state.onConnect,
  updateNodeField: state.updateNodeField,
});

export const PipelineUI = () => {
    const reactFlowWrapper = useRef(null);
    const [reactFlowInstance, setReactFlowInstance] = useState(null);
    const [minimapPosition, setMinimapPosition] = useState('bottom-right');

    const zoomToFit = useCallback(() => {
        if (reactFlowInstance) {
            reactFlowInstance.fitView({ padding: 0.1 });
        }
    }, [reactFlowInstance]);

    const toggleMinimapPosition = () => {
        const positions = ['bottom-right', 'bottom-left', 'top-right', 'top-left'];
        const currentIndex = positions.indexOf(minimapPosition);
        const nextIndex = (currentIndex + 1) % positions.length;
        setMinimapPosition(positions[nextIndex]);
    };

    const getMinimapPosition = () => {
        switch (minimapPosition) {
            case 'bottom-left': return 'bottom-left';
            case 'top-right': return 'top-right';
            case 'top-left': return 'top-left';
            default: return 'bottom-right';
        }
    };
    const {
      nodes,
      edges,
      getNodeID,
      addNode,
      onNodesChange,
      onEdgesChange,
      onConnect,
      updateNodeField,
    } = useStore(selector, shallow);

    const getInitNodeData = useCallback((nodeID, type) => {
      let nodeData = { id: nodeID, nodeType: `${type}` };
      return { ...nodeData, onChange: (fieldName, fieldValue) => updateNodeField(nodeID, fieldName, fieldValue) };
    }, [updateNodeField]);

    const onDrop = useCallback(
        (event) => {
          event.preventDefault();

          const reactFlowBounds = reactFlowWrapper.current.getBoundingClientRect();
          if (event?.dataTransfer?.getData('application/reactflow')) {
            const appData = JSON.parse(event.dataTransfer.getData('application/reactflow'));
            const type = appData?.nodeType;
            const templateData = appData?.templateData;

            // check if the dropped element is valid
            if (typeof type === 'undefined' || !type) {
              return;
            }

            const position = reactFlowInstance.project({
              x: event.clientX - reactFlowBounds.left,
              y: event.clientY - reactFlowBounds.top,
            });

            const nodeID = getNodeID(type);
            const initData = getInitNodeData(nodeID, type);
            const newNode = {
              id: nodeID,
              type,
              position,
              data: templateData ? { ...initData, ...templateData } : initData,
            };

            addNode(newNode);
          }
        },
        [reactFlowInstance, getNodeID, addNode, getInitNodeData]
    );

    const onDragOver = useCallback((event) => {
        event.preventDefault();
        event.dataTransfer.dropEffect = 'move';
    }, []);

    return (
        <>
        <div ref={reactFlowWrapper} style={{width: '100%', height: '100%'}}>
            <ReactFlow
                nodes={nodes}
                edges={edges}
                onNodesChange={onNodesChange}
                onEdgesChange={onEdgesChange}
                onConnect={onConnect}
                onDrop={onDrop}
                onDragOver={onDragOver}
                onInit={setReactFlowInstance}
                nodeTypes={nodeTypes}
                proOptions={proOptions}
                snapGrid={[gridSize, gridSize]}
                connectionLineType='smoothstep'
                deleteKeyCode="Delete"
            >
                <Background color="#aaa" gap={gridSize} />
                <Controls />
                <MiniMap
                    position={getMinimapPosition()}
                    style={{
                        backgroundColor: '#f8f9fa',
                        border: '1px solid #dee2e6'
                    }}
                    nodeColor={(node) => {
                        switch (node.type) {
                            case 'customInput': return '#4CAF50';
                            case 'llm': return '#2196F3';
                            case 'customOutput': return '#FF9800';
                            case 'text': return '#9C27B0';
                            case 'number': return '#FF5722';
                            case 'boolean': return '#795548';
                            case 'math': return '#607D8B';
                            case 'api': return '#00BCD4';
                            case 'delay': return '#8BC34A';
                            default: return '#ccc';
                        }
                    }}
                />
                <div className="minimap-controls">
                    <button
                        onClick={zoomToFit}
                        className="minimap-btn"
                        title="Zoom to fit"
                    >
                        üîç
                    </button>
                    <button
                        onClick={toggleMinimapPosition}
                        className="minimap-btn"
                        title="Change minimap position"
                    >
                        üìç
                    </button>
                </div>
            </ReactFlow>
        </div>
        </>
    )
}
